     List<Map> imgdb = DbUtils.getListMap(Db.getConn(), sql);                    DebugUtils.dump(imgdb);                    boolean esiste = false;                    if (imgdb.size() > 0) {                        esiste = true;                    }                    //controllo dimensioni                    File fupload = null;                    BufferedImage image = ImageIO.read(new File(file));                    File temp = null;                    if (image.getWidth() > 1400 || image.getHeight() > 1200) {                        //rimpicciolisco                        try {                            temp = new File(main.wd + "temp_img_articoli_upload/" + new File(file).getName());                            temp.getParentFile().mkdirs();                            temp.delete();                            image = ImgUtils.resizeQuality(image, ImgUtils.getDimension(image.getWidth(), image.getHeight(), 1400, 1200));                            ImgUtils.writeJpeg(image, temp.getAbsolutePath(), 0.9f);                            fupload = temp;                        } catch (Exception e) {                            e.printStackTrace();                            SwingUtils.showExceptionMessage(main.getPadreFrame(), e);                        }                    } else {                        fupload = new File(file);                    }                    HttpClient httpclient = new HttpClient();//                    PostMethod post = new PostMethod("http://indi.tnx.dyndns.org/perle_gitane/invoicex.php");                    PostMethod post = new PostMethod("http://www.perlegitane.it/invoicex.php");                    File f = new File(file);                    Part[] parts = {                        new StringPart("upload_img", "1"),                        new StringPart("articolo", texCodi.getText()),                        new StringPart("esiste", esiste ? "S" : "N"),                        new StringPart("esiste_codice", (String) (esiste ? imgdb.get(0).get("codice") : "")),                        new StringPart("esiste_immagine1", (String) (esiste ? imgdb.get(0).get("immagine1") : "")),                        new StringPart("esiste_immagine1_web", (String) (esiste ? imgdb.get(0).get("immagine1_web") : "")),                        new FilePart(f.getName(), fupload)                    };                    post.setRequestEntity(new MultipartRequestEntity(parts, post.getParams()));                    int ret = httpclient.executeMethod(post);                    System.out.println("ret = " + ret);                    String resp = post.getResponseBodyAsString();                    System.out.println("resp = " + resp);                    post.releaseConnection();                    if (temp != null) {                        temp.delete();                    }                } catch (Exception e) {                    e.printStackTrace();                }                return null;            }            @Override            protected void done() {                labStatusImg.setVisible(false);                labStatusImgWeb.setVisible(false);            }        };        worker.execute();    }    private void refreshImmagine() {        refreshImmagine(null);    }    synchronized public void refreshImmagine(final String file) {        final int w = labImmagine.getWidth();        final int h = labImmagine.getHeight();        conta_i++;        final int i = conta_i;        if (w > 0 && h > 0) {            Thread t = new Thread("refresh immagine") {                @Override                public void run() {                    try {                        String immagine = null;                        String immagine_web = null;//                        try {//                            immagine = StringUtils.replace(StringUtils.remove((String) dati.getCampiAggiuntivi().get("immagine1"), "'"), "\\\\", "\\");//                        } 