     if (griglia.getSelectedRowCount() > 1) {            SwingUtils.showErrorMessage(this, "Non è possibile eliminare più Clienti alla volta");            return;        }        //prima controllo se c'? ancora qualche fattura collegata        boolean clientePresente = false;        if (texCodi.getText().trim().length() > 0) {            String sql = "select cliente from test_fatt where cliente = " + Db.pc(this.texCodi.getText(), java.sql.Types.INTEGER);            ResultSet r = Db.openResultSet(sql);            try {                if (r.next()) {                    clientePresente = true;                    javax.swing.JOptionPane.showMessageDialog(this, "Il cliente non puo' essere eliminato perche' presente in una o piu' fatture", "Attenzione", javax.swing.JOptionPane.INFORMATION_MESSAGE);                } else {                    sql = "select cliente from test_ddt where cliente = " + Db.pc(this.texCodi.getText(), java.sql.Types.INTEGER);                    r = Db.openResultSet(sql);                    if (r.next()) {                        clientePresente = true;                        javax.swing.JOptionPane.showMessageDialog(this, "Il cliente non puo' essere eliminato perche' presente in uno o piu' ddt", "Attenzione", javax.swing.JOptionPane.INFORMATION_MESSAGE);                    }                }            } catch (Exception err) {                err.printStackTrace();            }        }        if (clientePresente == false) {            int ret = JOptionPane.showConfirmDialog(this, "Sicuro di eliminare ?", "Attenzione", JOptionPane.YES_NO_OPTION);            if (ret == JOptionPane.YES_OPTION) {                String codiceListino = (String) dati.dbGetField("codice_listino");                this.dati.dbDelete();                this.griglia.dbRefresh();                this.griglia.dbSelezionaRiga();                if (main.iniPrezziCliente == true) {                    if (codiceListino.startsWith("C")) {                        //elimino automaticamente il listino abbinato a questo cliente                        String sql = "delete from tipi_listino where codice = " + Db.pc(codiceListino, java.sql.Types.VARCHAR);                        Db.executeSql(sql);                        sql = "delete from articoli_prezzi where listino = " + Db.pc(codiceListino, java.sql.Types.VARCHAR);                        Db.executeSql(sql);                    }                }            }        }    }//GEN-LAST:event_butDeleActionPerformed    private void butFindActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butFindActionPerformed        boolean ret = this.griglia.dbFindNext();        if (ret == false) {            int ret2 = JOptionPane.showConfirmDialog(this, "Posizione non trovata\nVuoi riprovare dall'inizio ?", "Attenzione", JOptionPane.YES_NO_OPTION);            //JOptionPane.showMessageDialog(this,"?-:"+String.valueOf(i));            if (ret2 == JOptionPane.OK_OPTION) {                boolean ret3 = this.griglia.dbFindFirst();            }        }    }//GEN-LAST:event_butFindActionPerformed    private void butUndoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butUndoActionPerformed        dati.dbUndo();    }//GEN-LAST:event_butUndoActionPerformed    private void butSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butSaveActionPerformed        File ftest = new File(texLogo.getText());        if (texLogo.getText().length() > 0 && !ftest.exists()) {            SwingUtils.showWarningMessage(this, "Il file del logo non esiste!");        }        if (texLogo.getText().length() > 0 && ftest.length() > 1000000) {            SwingUtils.showWarningMessage(this, "Il file del logo è troppo grande, deve essere massimo 1Mb");            return;        }        texLogo.setText(texLogo.getText().replace("\\", "\\\\"));        //controlli vari//        //controlli validità//        if (texPiva.getText().length() != 11 && texPiva.getText().length() > 0) {//            if (!SwingUtils.showYesNoMessage(this, "La lunghezza della Partita IVA non è rispettata (deve essere 11 caratteri), continuare ?")) {//                return;//            }//        }//        if ((texCodiFisc.getText().length() != 16 && texCodiFisc.getText().length() != 11) && texCodiFisc.getText().length() > 0) {//            if (!SwingUtils.showYesNoMessage(this, "La lunghezza del Codice Fiscale non è rispettata (deve essere 16 o 11 caratteri), continuare ?")) {//                return;//            }//        }        if (!controlla(controlli)) {            return;        }        this.dati.dbSave();        //salvare i prezzi listino        this.griglia.dbRefresh();        if (this.comboToRefresh != null) {            this.comboToRefresh.dbRefreshItems();            this.dispose();        }        main.events.fireInvoicexEvent(new InvoicexEvent(this, InvoicexEvent.TYPE_SAVE));        if (padre != null) {            padre.texClie.setText(texCodi.getText());            padre.recuperaDatiCliente();        }    }//GEN-LAST:event_butSaveActionPerformed    private void butNewActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butNewActionPerformed        this.dati.dbNew();        //azzero prov pred e prov pred forn        provPred.setText("");        provPredForn.setText("");                //azzero banca e filaile        this.labBancAbi.setText("");        this.labBancCab.setText("");        java.sql.Statement stat;        ResultSet resu;        //apre il resultset per ultimo +1        try {            stat = Db.getConn().createStatement();            String sql = "select codice from clie_forn order by codice desc limit 1";            resu = stat.executeQuery(sql);            if (resu.next() == true) {                this.texCodi.setText(String.valueOf(resu.getInt(1) + 1));            } else {                this.tex